getStart.jsonArchitecture:
// Translation Data
    assets/i18n/get-start/zh-Hant.json
    {
    title: "Main Title",
    section1: {
      title: "Title for Section 1",
      subTitle: "Subtitle for Section 1"
    },
    footer: {
      title: "Footer Title",
      subTitle: "Footer Subtitle"
    }
  }

  // HTML Usage Example
  get-start.html
  <h1>{{ 'title' | t }}</h1>
  <h2>{{ 'section1.title' | t }}</h2>
  <h3>{{ 'section1.subTitle' | t }}</h3>

  // ts Usage Example
  get-start.ts
  mainTitle = translateService.t('title');

lazyLoad.compComp:
// The standalone component provides its namespace directly
@Component({
  selector: 'app-header',
  standalone: true,
  templateUrl: './header.component.html',
  providers: [
    provideTranslation('header'),  // The name must match the folder name.
  ],
})
export class HeaderComponent { }

lazyLoad.moduleComp:
// Traditional NgModule component using @Component.providers
@Component({
  selector: 'app-get-start',
  templateUrl: './get-start.component.html',
  providers: [
    provideTranslation('get-start'),  // The name must match the folder name.
  ],
})
export class GetStartComponent { }

lazyLoad.moduleModule:
// Provide the namespace at module level to share across components
@NgModule({
  declarations: [GetStartComponent],
  providers: [
    provideTranslation('get-start'),  // The name must match the folder name.
  ],
})
export class GetStartModule { }
