getStart.jsonArchitecture:
  // 翻譯資料
  assets/i18n/get-start/zh-Hant.json
{
  title: "大標題",
  section1: {
    title: "第一段標題",
    subTitle: "第一段副標題"
  },
  footer: {
    title: "結尾標題",
    subTitle: "結尾副標題"
  }
}

// html 使用範例
get-start.html
<h1>{{ 'title' | t }}</h1>
<h2>{{ 'section1.title' | t }}</h2>
<h3>{{ 'section1.subTitle' | t }}</h3>

// ts 使用範例
get-start.ts
mainTitle = translateService.t('title');

lazyLoad.compComp:
// Standalone 元件直接在 providers 設定對應 namespace
@Component({
  selector: 'app-header',
  standalone: true,
  templateUrl: './header.component.html',
  providers: [
    provideTranslation('header'), // 名稱必須與資料夾名稱相同
  ],
})
export class HeaderComponent { }

lazyLoad.moduleComp:
// 傳統 NgModule 元件一樣可以在 @Component.providers 設定
@Component({
  selector: 'app-get-start',
  templateUrl: './get-start.component.html',
  providers: [
    provideTranslation('get-start'), // 名稱必須與資料夾名稱相同
  ],
})
export class GetStartComponent { }

lazyLoad.moduleModule:
// 或者將 namespace provider 放在模組，讓此模組底下的元件共用
@NgModule({
  declarations: [GetStartComponent],
  providers: [
    provideTranslation('get-start'), // 名稱必須與資料夾名稱相同
  ],
})
export class GetStartModule { }
